        static int[] GetRandomNumber(int length)
        {
            List<int> route = new List<int>();
            Random rnd = new Random((int)DateTime.Now.Ticks);

            for (int i = 0; i < length; i++)
            {
                route.Add((rnd.Next(0, 300)));
            }

            return route.ToArray();
        }

        int[] nums;
        Dictionary<int, int> memo;
        public int Rob(int[] nums)
        {
            this.nums = nums;

            //return BruteForce(0);

            //this.memo = new Dictionary<int, int>();
            //return Math.Max(MemoBruteForce2(0), MemoBruteForce2(1));
            //return Iterative();

            return IterativeLightMemo();
        }

        private int BruteForce(int index)
        {
            if (index >= nums.Length) return 0;
            else
            {
                int amount1 = nums[index] + BruteForce(index + 2);

                int amount2 = BruteForce(index + 1);

                return Math.Max(amount1, amount2);
            }
        }
        
        private int MemoBruteForce(int index)
        {
            if (index >= nums.Length) return 0;
            else if (memo.ContainsKey(index))
            {
                return memo[index];
            }
            else
            {
                int amount1 = nums[index] + MemoBruteForce(index + 2);
                int amount2 = nums[index] + MemoBruteForce(index + 3);

                memo.Add(index, Math.Max(amount1, amount2));
                return memo[index];
            }
        }

        private int Iterative()
        {
            if (nums.Length == 0) return 0;
            else if (nums.Length == 1) return nums[0];

            memo[-1] = 0;
            memo[0] = nums[0];
            memo[1] = nums[1];
            for (int i = 2; i < nums.Length; i++)
            {
                memo[i] = Math.Max(memo[i - 2] + nums[i],
                                        memo[i - 3] + nums[i]);
            }

            return memo.Values.Max();
        }

        private int IterativeLightMemo()
        {
            if (nums.Length == 0) return 0;

            int memo1 = nums[0];
            int memo2 = 0;
            int memo3 = 0;
            int maxAmount = nums[0];
            for (int i = 1; i < nums.Length; i++)
            {
                int currentAmount = Math.Max(memo2 + nums[i], memo3 + nums[i]);
                maxAmount = Math.Max(currentAmount, maxAmount);

                memo3 = memo2;
                memo2 = memo1;
                memo1 = currentAmount;
            }

            return maxAmount;
        }
    }
